define("local_bulkcertdownload/main",["jquery"],function($){return{init:function(){$("#id_courseid").change(function(){var courseid=$(this).val();var $cert=$("#id_certid");var $group=$("#id_groupid");if(courseid==0){$cert.html('<option value="0">Seleccionar certificado</option>');$group.html('<option value="0">Seleccionar grupo</option>');return}$.getJSON(M.cfg.wwwroot+"/local/bulkcertdownload/ajax.php",{action:"getcertificates",courseid:courseid},function(response){var options='<option value="0">Seleccionar certificado</option>';$.each(response.customcerts,function(id,name){options+='<option value="'+id+'">'+name+"</option>"});$cert.html(options)});$.getJSON(M.cfg.wwwroot+"/local/bulkcertdownload/ajax.php",{action:"getgroups",courseid:courseid},function(response){var options='<option value="0">Seleccionar grupo</option>';$.each(response.groups,function(id,name){options+='<option value="'+id+'">'+name+"</option>"});$group.html(options)})})}}});
